var Mors = {
    hle: 70,
    led: 25567.5,
    etr: function(a) {
        return this.led - this.ndl(a)
    },
    eyd: function(a) {
        return a.getFullYear() + this.hle
    },
    pro: function(a) {
        return this.ndl(a) / this.led * 100
    },
    ndl: function(a) {
        return Math.round(Math.abs((a.getTime() - (new Date).getTime()) / 864E5))
    }
}